theme.customCSS:
.widget-text {
  font-size: 0.8rem;
  color: rgba(255, 255, 255, 0.6);
  text-align: center;
}

.widget-dailygoal .btn {
  visibility: hidden;
}

.widget-dailygoal:hover .btn {
  visibility: inherit;
}

.widget-dailygoal .input-auto input[value=""] {
  border-color: transparent;
}

.widget-dailygoal:hover .input-auto input[value=""] {
  border-color: rgba(255, 255, 255, 0.5);
}

.todolist {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
}

.todolist li {
  padding: 0;
  align-items: center;
}

.todolist li:last-child {
  display: none;
}

.todolist:hover li:last-child {
  display: inherit;
  padding-right: 1.5em;
}

.todolist input {
  margin: 0;
  padding: 0;
  color: rgba(255, 255, 255, 0.8);
}

.todolist input[type="checkbox"] {
  visibility: hidden;
  margin: 0;
}

.todolist input[type="checkbox"]:disabled {
  visibility: hidden;
}

.todolist li:hover input[type="checkbox"]:not(:disabled) {
  visibility: inherit;
}

.todolist input[value=""] {
  border-bottom: 2px solid rgba(255, 255, 255, 0.2);
}

.todolist li:last-child .backing {
  min-width: 5em;
}

.todolist input:focus, .todolist input:hover {
  border-bottom: 2px solid rgba(255, 255, 255, 0.5);
}
